<span class="line"><span style="color:#82AAFF;">print</span><span style="color:#A6ACCD;">(arr[</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">1</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">])</span><span style="color:#89DDFF;">;</span><span style="color:#A6ACCD;">   </span><span style="color:#676E95;font-style:italic;">// refers to &#39;bar&#39;</span></span>
