import{_ as p,c as n,o,a3 as s}from"./chunks/framework.DflgEAq4.js";const _=JSON.parse('{"title":"システム変数","description":"","frontmatter":{},"headers":[],"relativePath":"translation/hsp/sysval.md","filePath":"translation/hsp/sysval.md","lastUpdated":1676126774000}'),a={name:"translation/hsp/sysval.md"},r=s(`<h1 id="システム変数" tabindex="-1">システム変数 <a class="header-anchor" href="#システム変数" aria-label="Permalink to &quot;システム変数&quot;">​</a></h1><p><strong>対応</strong>:</p><ul><li>Win</li><li>Cli</li><li>Let</li></ul><h2 id="hspstat" tabindex="-1">hspstat <a class="header-anchor" href="#hspstat" aria-label="Permalink to &quot;hspstat {#hspstat}&quot;">​</a></h2><p>HSPランタイムの情報</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>HSPランタイムの情報が整数値で代入されます。以下の情報がすべて合計された値になります。</p><ul><li>デバッグモード = 1</li><li>スクリーンセーバー起動時 = 2</li><li>コンソールモード = 16</li><li>Macintosh版HSP = $80</li><li>HSP3Dish = $100</li><li>Linux版HSP = $1000</li><li>UTF8文字列ランタイム = $20000</li><li>64ビットランタイム = $40000</li></ul><p><strong>参照</strong>:</p><p>_debug</p><h2 id="hspver" tabindex="-1">hspver <a class="header-anchor" href="#hspver" aria-label="Permalink to &quot;hspver {#hspver}&quot;">​</a></h2><p>HSPのバージョン番号</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>HSP のバージョン番号が整数値で代入されます。バージョンコード + マイナーバージョンコードの値になります。(3.0 は $3??? になる)</p><p><strong>参照</strong>:</p><p><strong>hspver</strong></p><h2 id="cnt" tabindex="-1">cnt <a class="header-anchor" href="#cnt" aria-label="Permalink to &quot;cnt {#cnt}&quot;">​</a></h2><p>ループのカウンター</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>repeat〜loopループのカウンターが整数値で代入されます。</p><p><strong>参照</strong>:</p><p>repeat foreach loop</p><h2 id="err" tabindex="-1">err <a class="header-anchor" href="#err" aria-label="Permalink to &quot;err {#err}&quot;">​</a></h2><p>HSPのエラーコード</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>HSPのエラーコードです。エラーコードとメッセージの対応については、別途マニュアル(error.htm)を参照してください。</p><p><strong>参照</strong>:</p><p>onerror</p><h2 id="stat" tabindex="-1">stat <a class="header-anchor" href="#stat" aria-label="Permalink to &quot;stat {#stat}&quot;">​</a></h2><p>色々な命令のステータス値</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>色々な命令を実行した結果(ステータス)が整数値で代入されます。</p><p><strong>参照</strong>:</p><p>return dialog mref</p><h2 id="mousex" tabindex="-1">mousex <a class="header-anchor" href="#mousex" aria-label="Permalink to &quot;mousex {#mousex}&quot;">​</a></h2><p>マウスカーソルのX座標</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>マウスカーソルのX座標が整数値で代入されます。</p><p><strong>例</strong>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>; マウスポインタの動きに沿って線を描く</span></span>
<span class="line"><span>repeat</span></span>
<span class="line"><span>	line mousex, mousey</span></span>
<span class="line"><span>	wait 1</span></span>
<span class="line"><span>	loop</span></span></code></pre></div><p><strong>参照</strong>:</p><p>mouse mousey mousew ginfo_mx</p><h2 id="mousey" tabindex="-1">mousey <a class="header-anchor" href="#mousey" aria-label="Permalink to &quot;mousey {#mousey}&quot;">​</a></h2><p>マウスカーソルのY座標</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>マウスカーソルのY座標が整数値で代入されます。</p><p><strong>例</strong>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>; マウスポインタの動きに沿って線を描く</span></span>
<span class="line"><span>repeat</span></span>
<span class="line"><span>	line mousex, mousey</span></span>
<span class="line"><span>	wait 1</span></span>
<span class="line"><span>	loop</span></span></code></pre></div><p><strong>参照</strong>:</p><p>mouse mousex mousew ginfo_my</p><h2 id="mousew" tabindex="-1">mousew <a class="header-anchor" href="#mousew" aria-label="Permalink to &quot;mousew {#mousew}&quot;">​</a></h2><p>マウスカーソルのホイール値</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>ホイール付きマウスの移動量が整数値で代入されます。</p><p><strong>参照</strong>:</p><p>mousex mousey</p><p><strong>非対応</strong>:</p><ul><li>Let</li></ul><h2 id="strsize" tabindex="-1">strsize <a class="header-anchor" href="#strsize" aria-label="Permalink to &quot;strsize {#strsize}&quot;">​</a></h2><p>読み出しバイト数</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>getstr、exist、bload命令などで読み出したバイト数が代入されます。</p><p><strong>参照</strong>:</p><p>bload exist getstr</p><h2 id="refstr" tabindex="-1">refstr <a class="header-anchor" href="#refstr" aria-label="Permalink to &quot;refstr {#refstr}&quot;">​</a></h2><p>色々な命令のステータス文字列</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>色々な命令を実行した結果の文字列が代入されます。</p><p><strong>参照</strong>:</p><p>return dialog mref</p><h2 id="looplev" tabindex="-1">looplev <a class="header-anchor" href="#looplev" aria-label="Permalink to &quot;looplev {#looplev}&quot;">​</a></h2><p>repeatのネストレベル</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>repeat〜loopのネストレベルが代入されます。</p><p><strong>参照</strong>:</p><p>repeat foreach loop</p><h2 id="sublev" tabindex="-1">sublev <a class="header-anchor" href="#sublev" aria-label="Permalink to &quot;sublev {#sublev}&quot;">​</a></h2><p>サブルーチンのネストレベル</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>サブルーチン(またはユーザー定義命令、関数) のネストレベルが代入されます。</p><p><strong>参照</strong>:</p><p>gosub return</p><h2 id="iparam" tabindex="-1">iparam <a class="header-anchor" href="#iparam" aria-label="Permalink to &quot;iparam {#iparam}&quot;">​</a></h2><p>割り込み要因を示す値</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>割り込み要因を示す値が代入されます。 oncmd命令による割り込み時は、割り込みメッセージのIDが代入されます。 onexit命令による終了割り込み時は、通常のウィンドウクローズによる終了であれば0が、シャットダウン等のシステム通知による終了であれば1が代入されます。</p><p><strong>参照</strong>:</p><p>onkey onclick onexit lparam wparam onerror oncmd</p><p><strong>例</strong>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>	onkey goto *lab</span></span>
<span class="line"><span>	stop</span></span>
<span class="line"><span>*lab</span></span>
<span class="line"><span>	mes &quot;キーコード : &quot;+wparam+&quot;, 文字コード : &quot;+iparam</span></span>
<span class="line"><span>	stop</span></span></code></pre></div><p><strong>非対応</strong>:</p><ul><li>Let</li></ul><h2 id="wparam" tabindex="-1">wparam <a class="header-anchor" href="#wparam" aria-label="Permalink to &quot;wparam {#wparam}&quot;">​</a></h2><p>割り込み時のwParam</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>割り込み時に保存される Windows のシステム値 (wParam) が代入されます。</p><p><strong>参照</strong>:</p><p>onkey onclick onerror iparam lparam onerror oncmd</p><p><strong>例</strong>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>	onkey goto *lab</span></span>
<span class="line"><span>	stop</span></span>
<span class="line"><span>*lab</span></span>
<span class="line"><span>	a = lparam&gt;&gt;24&amp;1</span></span>
<span class="line"><span>	mes &quot;キーコード : &quot;+wparam+&quot;, 拡張キーフラグ : &quot;+a</span></span>
<span class="line"><span>	stop</span></span></code></pre></div><p><strong>非対応</strong>:</p><ul><li>Let</li></ul><h2 id="lparam" tabindex="-1">lparam <a class="header-anchor" href="#lparam" aria-label="Permalink to &quot;lparam {#lparam}&quot;">​</a></h2><p>割り込み時のlParam</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>割り込み時に保存される Windows のシステム値 (lParam) が代入されます。</p><p><strong>参照</strong>:</p><p>onkey onclick onerror iparam wparam onerror oncmd</p><p><strong>例</strong>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>	onkey goto *lab</span></span>
<span class="line"><span>	stop</span></span>
<span class="line"><span>*lab</span></span>
<span class="line"><span>	a = lparam&gt;&gt;24&amp;1</span></span>
<span class="line"><span>	mes &quot;キーコード : &quot;+wparam+&quot;, 拡張キーフラグ : &quot;+a</span></span>
<span class="line"><span>	stop</span></span></code></pre></div><p><strong>非対応</strong>:</p><ul><li>Let</li></ul><h2 id="hwnd" tabindex="-1">hwnd <a class="header-anchor" href="#hwnd" aria-label="Permalink to &quot;hwnd {#hwnd}&quot;">​</a></h2><p>現在のウィンドウハンドル</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在選択されているウィンドウのハンドル(ポインタ)が代入されます。 主にDLL等の外部呼出しで必要な場合に参照されます。</p><p><strong>参照</strong>:</p><p>mref</p><h2 id="hdc" tabindex="-1">hdc <a class="header-anchor" href="#hdc" aria-label="Permalink to &quot;hdc {#hdc}&quot;">​</a></h2><p>現在のデバイスコンテキスト</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在選択されているウィンドウのデバイスコンテキスト(ポインタ)が代入されます。 主にDLL等の外部呼出しで必要な場合に参照されます。</p><p><strong>参照</strong>:</p><p>mref</p><h2 id="hinstance" tabindex="-1">hinstance <a class="header-anchor" href="#hinstance" aria-label="Permalink to &quot;hinstance {#hinstance}&quot;">​</a></h2><p>現在のインスタンスハンドル</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在実行中アプリケーションのインスタンスハンドル(ポインタ)が代入されます。 主にDLL等の外部呼出しで必要な場合に参照されます。</p><p><strong>参照</strong>:</p><p>mref <strong>非対応</strong>:</p><ul><li>Let</li></ul><h2 id="refdval" tabindex="-1">refdval <a class="header-anchor" href="#refdval" aria-label="Permalink to &quot;refdval {#refdval}&quot;">​</a></h2><p>色々な命令のステータス実数値</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>色々な命令を実行した結果の実数値が代入されます。</p><p><strong>参照</strong>:</p><p>return</p><h2 id="thismod" tabindex="-1">thismod <a class="header-anchor" href="#thismod" aria-label="Permalink to &quot;thismod {#thismod}&quot;">​</a></h2><p>現在の有効なモジュール変数</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>モジュール変数処理のためのユーザー定義命令・関数内で、 渡されたモジュール変数自身を示す名称として使用することができます。 #modfuncで定義されたルーチンから、別な命令・関数を呼び出す場合に使用することができます。</p><p><strong>参照</strong>:</p><p>newmod delmod</p><p><strong>非対応</strong>:</p><ul><li>Let</li></ul><h2 id="notemax" tabindex="-1">notemax <a class="header-anchor" href="#notemax" aria-label="Permalink to &quot;notemax {#notemax}&quot;">​</a></h2><p>メモリノートパッドの行数</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在選択されているメモリノートパッド全体の行数を示すシステム変数として使用することができます。 noteinfo関数でも同様の情報を取得することが可能です。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>noteinfo</p><h2 id="notesize" tabindex="-1">notesize <a class="header-anchor" href="#notesize" aria-label="Permalink to &quot;notesize {#notesize}&quot;">​</a></h2><p>メモリノートパッドの文字数</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在選択されているメモリノートパッド全体の文字数(byte数)を示す システム変数として使用することができます。 noteinfo関数でも同様の情報を取得することが可能です。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>noteinfo</p><h2 id="ginfo_mx" tabindex="-1">ginfo_mx <a class="header-anchor" href="#ginfo_mx" aria-label="Permalink to &quot;ginfo_mx {#ginfo_mx}&quot;">​</a></h2><p>スクリーン上のマウスカーソルX座標</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>スクリーン上のマウスカーソルX座標が代入されています。 スクリーン座標系は、 pos命令などで使用するウィンドウ内の座標ではなく、デスクトップ画面全体から見た座標になります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo ginfo_my</p><h2 id="ginfo_my" tabindex="-1">ginfo_my <a class="header-anchor" href="#ginfo_my" aria-label="Permalink to &quot;ginfo_my {#ginfo_my}&quot;">​</a></h2><p>スクリーン上のマウスカーソルY座標</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>スクリーン上のマウスカーソルY座標が代入されています。 スクリーン座標系は、 pos命令などで使用するウィンドウ内の座標ではなく、デスクトップ画面全体から見た座標になります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo ginfo_mx</p><h2 id="ginfo_act" tabindex="-1">ginfo_act <a class="header-anchor" href="#ginfo_act" aria-label="Permalink to &quot;ginfo_act {#ginfo_act}&quot;">​</a></h2><p>アクティブなウィンドウID</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在アクティブになっているウィンドウIDが代入されています。 もし、 アクティブになっているウィンドウがHSP以外の場合は-1になります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo</p><h2 id="ginfo_sel" tabindex="-1">ginfo_sel <a class="header-anchor" href="#ginfo_sel" aria-label="Permalink to &quot;ginfo_sel {#ginfo_sel}&quot;">​</a></h2><p>操作先ウィンドウID</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>gsel命令で指定した画面の操作先ウィンドウIDが代入されています。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>gsel ginfo</p><h2 id="ginfo_wx1" tabindex="-1">ginfo_wx1 <a class="header-anchor" href="#ginfo_wx1" aria-label="Permalink to &quot;ginfo_wx1 {#ginfo_wx1}&quot;">​</a></h2><p>ウィンドウの左上X座標</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>スクリーン座標系で現在のウィンドウの左上X座標が代入されています。 スクリーン座標系は、 pos命令などで使用するウィンドウ内の座標ではなく、デスクトップ画面全体から見た座標になります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_wy1 ginfo_wx2 ginfo_wy2 ginfo</p><h2 id="ginfo_wy1" tabindex="-1">ginfo_wy1 <a class="header-anchor" href="#ginfo_wy1" aria-label="Permalink to &quot;ginfo_wy1 {#ginfo_wy1}&quot;">​</a></h2><p>ウィンドウの左上Y座標</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>スクリーン座標系で現在のウィンドウの左上Y座標が代入されています。 スクリーン座標系は、 pos命令などで使用するウィンドウ内の座標ではなく、デスクトップ画面全体から見た座標になります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_wx1 ginfo_wx2 ginfo_wy2 ginfo</p><h2 id="ginfo_wx2" tabindex="-1">ginfo_wx2 <a class="header-anchor" href="#ginfo_wx2" aria-label="Permalink to &quot;ginfo_wx2 {#ginfo_wx2}&quot;">​</a></h2><p>ウィンドウの右下X座標</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>スクリーン座標系で現在のウィンドウの右下X座標が代入されています。 スクリーン座標系は、 pos命令などで使用するウィンドウ内の座標ではなく、デスクトップ画面全体から見た座標になります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_wy2 ginfo_wx1 ginfo_wy1 ginfo</p><h2 id="ginfo_wy2" tabindex="-1">ginfo_wy2 <a class="header-anchor" href="#ginfo_wy2" aria-label="Permalink to &quot;ginfo_wy2 {#ginfo_wy2}&quot;">​</a></h2><p>ウィンドウの右下Y座標</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>スクリーン座標系で現在のウィンドウの右下Y座標が代入されています。 スクリーン座標系は、 pos命令などで使用するウィンドウ内の座標ではなく、デスクトップ画面全体から見た座標になります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_wx2 ginfo_wx1 ginfo_wy1 ginfo</p><h2 id="ginfo_vx" tabindex="-1">ginfo_vx <a class="header-anchor" href="#ginfo_vx" aria-label="Permalink to &quot;ginfo_vx {#ginfo_vx}&quot;">​</a></h2><p>ウィンドウのスクロールX座標</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在の操作先ウィンドウのスクロールX座標が代入されています。 これは、初期化サイズよりもウィンドウサイズが小さくなっている場合に、左上に表示されている座標がどれだけスクロールされているかを示す値です。 スクロールさせていない通常の状態では、(0,0)になります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_vy ginfo</p><h2 id="ginfo_vy" tabindex="-1">ginfo_vy <a class="header-anchor" href="#ginfo_vy" aria-label="Permalink to &quot;ginfo_vy {#ginfo_vy}&quot;">​</a></h2><p>ウィンドウのスクロールY座標</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在の操作先ウィンドウのスクロールY座標が代入されています。 これは、初期化サイズよりもウィンドウサイズが小さくなっている場合に、左上に表示されている座標がどれだけスクロールされているかを示す値です。 スクロールさせていない通常の状態では、(0,0)になります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_vx ginfo</p><h2 id="ginfo_sizex" tabindex="-1">ginfo_sizex <a class="header-anchor" href="#ginfo_sizex" aria-label="Permalink to &quot;ginfo_sizex {#ginfo_sizex}&quot;">​</a></h2><p>ウィンドウ全体のXサイズ</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在の操作先ウィンドウの横幅が代入されています。 これは、ウィンドウの枠やタイトルバーなども含めたサイズになります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_sizey ginfo</p><h2 id="ginfo_sizey" tabindex="-1">ginfo_sizey <a class="header-anchor" href="#ginfo_sizey" aria-label="Permalink to &quot;ginfo_sizey {#ginfo_sizey}&quot;">​</a></h2><p>ウィンドウ全体のYサイズ</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在の操作先ウィンドウの縦幅が代入されています。 これは、ウィンドウの枠やタイトルバーなども含めたサイズになります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_sizex ginfo</p><h2 id="ginfo_winx" tabindex="-1">ginfo_winx <a class="header-anchor" href="#ginfo_winx" aria-label="Permalink to &quot;ginfo_winx {#ginfo_winx}&quot;">​</a></h2><p>画面の描画エリアXサイズ</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在のウィンドウ描画エリアの横幅サイズが代入されています。 (buffer命令で作成された画面の場合は、初期化された画面サイズになります。)</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_winy ginfo</p><h2 id="ginfo_winy" tabindex="-1">ginfo_winy <a class="header-anchor" href="#ginfo_winy" aria-label="Permalink to &quot;ginfo_winy {#ginfo_winy}&quot;">​</a></h2><p>画面の描画エリアYサイズ</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在のウィンドウ描画エリアの縦幅サイズが代入されています。 (buffer命令で作成された画面の場合は、初期化された画面サイズになります。)</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_winx ginfo</p><h2 id="ginfo_sx" tabindex="-1">ginfo_sx <a class="header-anchor" href="#ginfo_sx" aria-label="Permalink to &quot;ginfo_sx {#ginfo_sx}&quot;">​</a></h2><p>画面の初期化Xサイズ</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在の操作先ウィンドウの初期化Xサイズが代入されています。 (最初にscreen,bgscr,buffer命令で初期化された画面サイズになります。)</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_sy ginfo</p><h2 id="ginfo_sy" tabindex="-1">ginfo_sy <a class="header-anchor" href="#ginfo_sy" aria-label="Permalink to &quot;ginfo_sy {#ginfo_sy}&quot;">​</a></h2><p>画面の初期化Yサイズ</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在の操作先ウィンドウの初期化Yサイズが代入されています。 (最初にscreen,bgscr,buffer命令で初期化された画面サイズになります。)</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_sx ginfo</p><h2 id="ginfo_mesx" tabindex="-1">ginfo_mesx <a class="header-anchor" href="#ginfo_mesx" aria-label="Permalink to &quot;ginfo_mesx {#ginfo_mesx}&quot;">​</a></h2><p>メッセージの出力Xサイズ</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>最後にmes, print命令により出力されたメッセージのXサイズが代入されています。 サイズは、 X座標にどれだけのドット数で描画されたかを示します。 画面上の絶対座標ではないので注意してください。 また、複数行ある文字列を出力した場合は、最後の行にあたるサイズが取得されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_mesy ginfo</p><h2 id="ginfo_mesy" tabindex="-1">ginfo_mesy <a class="header-anchor" href="#ginfo_mesy" aria-label="Permalink to &quot;ginfo_mesy {#ginfo_mesy}&quot;">​</a></h2><p>メッセージの出力Yサイズ</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>最後にmes, print命令により出力されたメッセージのYサイズが代入されています。 サイズは、 Y座標にどれだけのドット数で描画されたかを示します。 画面上の絶対座標ではないので注意してください。 また、複数行ある文字列を出力した場合は、最後の行にあたるサイズが取得されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_mesx ginfo</p><h2 id="ginfo_r" tabindex="-1">ginfo_r <a class="header-anchor" href="#ginfo_r" aria-label="Permalink to &quot;ginfo_r {#ginfo_r}&quot;">​</a></h2><p>現在設定されているカラーコードR</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>color命令などにより指定された赤の色コード(輝度)が代入されています。 色コードは、0〜255までの整数値になります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_g ginfo_b ginfo</p><h2 id="ginfo_g" tabindex="-1">ginfo_g <a class="header-anchor" href="#ginfo_g" aria-label="Permalink to &quot;ginfo_g {#ginfo_g}&quot;">​</a></h2><p>現在設定されているカラーコードG</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>color命令などにより指定された緑の色コード(輝度)が代入されています。 色コードは、0〜255までの整数値になります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_r ginfo_b ginfo</p><h2 id="ginfo_b" tabindex="-1">ginfo_b <a class="header-anchor" href="#ginfo_b" aria-label="Permalink to &quot;ginfo_b {#ginfo_b}&quot;">​</a></h2><p>現在設定されているカラーコードB</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>color命令などにより指定された青の色コード(輝度)が代入されています。 色コードは、0〜255までの整数値になります。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_r ginfo_g ginfo</p><h2 id="ginfo_paluse" tabindex="-1">ginfo_paluse <a class="header-anchor" href="#ginfo_paluse" aria-label="Permalink to &quot;ginfo_paluse {#ginfo_paluse}&quot;">​</a></h2><p>デスクトップのカラーモード</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在のデスクトップカラーモード(色モード)が代入されています。 フルカラーモードの場合は0が、パレットモードの場合は1が返されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo</p><h2 id="ginfo_dispx" tabindex="-1">ginfo_dispx <a class="header-anchor" href="#ginfo_dispx" aria-label="Permalink to &quot;ginfo_dispx {#ginfo_dispx}&quot;">​</a></h2><p>デスクトップ全体のXサイズ</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>デスクトップ全体のサイズ(画面解像度)が代入されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_dispy ginfo</p><h2 id="ginfo_dispy" tabindex="-1">ginfo_dispy <a class="header-anchor" href="#ginfo_dispy" aria-label="Permalink to &quot;ginfo_dispy {#ginfo_dispy}&quot;">​</a></h2><p>デスクトップ全体のYサイズ</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>デスクトップ全体のサイズ(画面解像度)が代入されています。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_dispx ginfo</p><h2 id="ginfo_cx" tabindex="-1">ginfo_cx <a class="header-anchor" href="#ginfo_cx" aria-label="Permalink to &quot;ginfo_cx {#ginfo_cx}&quot;">​</a></h2><p>カレントポジションのX座標</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>pos命令により設定されたカレントポジションのX座標が代入されています。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_cy ginfo</p><h2 id="ginfo_cy" tabindex="-1">ginfo_cy <a class="header-anchor" href="#ginfo_cy" aria-label="Permalink to &quot;ginfo_cy {#ginfo_cy}&quot;">​</a></h2><p>カレントポジションのY座標</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>pos命令により設定されたカレントポジションのY座標が代入されています。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo_cx ginfo</p><h2 id="ginfo_intid" tabindex="-1">ginfo_intid <a class="header-anchor" href="#ginfo_intid" aria-label="Permalink to &quot;ginfo_intid {#ginfo_intid}&quot;">​</a></h2><p>メッセージ割り込み時のウィンドウID</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>oncmd命令により設定されたメッセージ割り込み時のウィンドウIDが代入されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>oncmd ginfo</p><h2 id="ginfo_newid" tabindex="-1">ginfo_newid <a class="header-anchor" href="#ginfo_newid" aria-label="Permalink to &quot;ginfo_newid {#ginfo_newid}&quot;">​</a></h2><p>未使用ウィンドウID</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>screen命令やbuffer命令などで初期化されていない未使用のウィンドウIDが返されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo</p><h2 id="ginfo_accx" tabindex="-1">ginfo_accx <a class="header-anchor" href="#ginfo_accx" aria-label="Permalink to &quot;ginfo_accx {#ginfo_accx}&quot;">​</a></h2><p>加速度センサー値(X)</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>加速度センサーが使用可能な環境で、センサーの値を取得します。 X軸に対する加速度を-1.0〜+1.0の範囲で取得したものになります。 加速度センサーが使用できない環境では、0.0が取得されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo ginfo_accy ginfo_accz</p><h2 id="ginfo_accy" tabindex="-1">ginfo_accy <a class="header-anchor" href="#ginfo_accy" aria-label="Permalink to &quot;ginfo_accy {#ginfo_accy}&quot;">​</a></h2><p>加速度センサー値(Y)</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>加速度センサーが使用可能な環境で、センサーの値を取得します。 Y軸に対する加速度を-1.0〜+1.0の範囲で取得したものになります。 加速度センサーが使用できない環境では、0.0が取得されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo ginfo_accx ginfo_accz</p><h2 id="ginfo_accz" tabindex="-1">ginfo_accz <a class="header-anchor" href="#ginfo_accz" aria-label="Permalink to &quot;ginfo_accz {#ginfo_accz}&quot;">​</a></h2><p>加速度センサー値(Z)</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>加速度センサーが使用可能な環境で、センサーの値を取得します。 Z軸に対する加速度を-1.0〜+1.0の範囲で取得したものになります。 加速度センサーが使用できない環境では、0.0が取得されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>ginfo ginfo_accy ginfo_accx</p><h2 id="dir_cur" tabindex="-1">dir_cur <a class="header-anchor" href="#dir_cur" aria-label="Permalink to &quot;dir_cur {#dir_cur}&quot;">​</a></h2><p>カレントディレクトリ(フォルダ)</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>現在のカレントディレクトリ(フォルダ)のパスが文字列で代入されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>dirinfo</p><h2 id="dir_exe" tabindex="-1">dir_exe <a class="header-anchor" href="#dir_exe" aria-label="Permalink to &quot;dir_exe {#dir_exe}&quot;">​</a></h2><p>実行ファイルがあるディレクトリ(フォルダ)</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>ランタイム実行ファイルがあるディレクトリ(フォルダ)のパスが文字列で代入されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>dirinfo</p><h2 id="dir_win" tabindex="-1">dir_win <a class="header-anchor" href="#dir_win" aria-label="Permalink to &quot;dir_win {#dir_win}&quot;">​</a></h2><p>Windowsディレクトリ(フォルダ)</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>Windowsがインストールされているディレクトリ(フォルダ)のパスが文字列で代入されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>dirinfo</p><h2 id="dir_sys" tabindex="-1">dir_sys <a class="header-anchor" href="#dir_sys" aria-label="Permalink to &quot;dir_sys {#dir_sys}&quot;">​</a></h2><p>Windowsシステムディレクトリ(フォルダ)</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>Windowsのシステムディレクトリ(フォルダ)のパスが文字列で代入されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>dirinfo</p><h2 id="dir_cmdline" tabindex="-1">dir_cmdline <a class="header-anchor" href="#dir_cmdline" aria-label="Permalink to &quot;dir_cmdline {#dir_cmdline}&quot;">​</a></h2><p>コマンドライン文字列</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>実行時に渡されたコマンドライン文字列が代入されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>dirinfo</p><h2 id="dir_desktop" tabindex="-1">dir_desktop <a class="header-anchor" href="#dir_desktop" aria-label="Permalink to &quot;dir_desktop {#dir_desktop}&quot;">​</a></h2><p>デスクトップディレクトリ(フォルダ)</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>デスクトップディレクトリ(フォルダ)のパスが文字列で代入されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>dirinfo</p><h2 id="dir_mydoc" tabindex="-1">dir_mydoc <a class="header-anchor" href="#dir_mydoc" aria-label="Permalink to &quot;dir_mydoc {#dir_mydoc}&quot;">​</a></h2><p>マイドキュメントディレクトリ(フォルダ)</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>マイドキュメントディレクトリ(フォルダ)のパスが文字列で代入されます。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>dirinfo</p><h2 id="dir_tv" tabindex="-1">dir_tv <a class="header-anchor" href="#dir_tv" aria-label="Permalink to &quot;dir_tv {#dir_tv}&quot;">​</a></h2><p>HSPTVディレクトリ(フォルダ)</p><p><strong>グループ</strong>:</p><p>システム変数</p><p><strong>説明</strong>:</p><p>HSPTVディレクトリ(フォルダ)のパスが文字列で代入されます。 HSPTVディレクトリに標準で用意されたリソースを参照する場合に使用することができます。 dir_tvは、デバッグ実行時にのみ取得され、実行(.exe)ファイルとして動作している場合は、空の文字列(&quot;&quot;)を返します。HSPTVディレクトリ内のファイルを使用するソフトの実行ファイル作成時は、#pack命令により必要なファイルを指定する必要があるので注意してください。</p><p>※このシステム変数は、マクロとしてhspdef.as内で定義されています。</p><p><strong>参照</strong>:</p><p>dirinfo</p>`,554),t=[r];function i(e,g,l,h,d,f){return o(),n("div",null,t)}const u=p(a,[["render",i]]);export{_ as __pageData,u as default};
